<?php/** * This example shows making an SMTP connection with authentication. *///SMTP needs accurate times, and the PHP time zone MUST be set//This should be done in your php.ini, but this is how to do it if you don't have access to that//date_default_timezone_set('Etc/UTC');require APP_INCLUDE_Library.'smtp_mail/PHPMailerAutoload.php';//require APP_INCLUDE_Library.'smtp_mail/Security.php';class SMTP_mail {    public $mail;    public $sender_email;    public $username;    public $password;    public $host;    public $port;    public $subject;       public $sender_name;   // public $product_name;    public function __construct() {        $this->mail = new PHPMailer;        $this->port = 465;        $this->host = "mail.codexives.com";               $this->username = "info@codexives.com";        $this->password = "jeny7001@";                                     }public function inviteMail($friendEmail) {                                            $this->sender_email ='info@codexives.com';         $this->sender_name ='Linkpan';                $this->subject ='Invitation mail';        $this->mail->isSMTP();        $this->mail->SMTPDebug = 0;        $this->mail->Debugoutput = 'html';        $this->mail->Host = $this->host;        $this->mail->Port = $this->port;        $this->mail->SMTPAuth = true;        $this->mail->SMTPSecure = true;        $this->mail->Username = $this->username;        $this->mail->Password = $this->password;        $this->mail->setFrom($this->sender_email);        $this->mail->addReplyTo($this->sender_email);        foreach ($friendEmail as $row){            $this->mail->addAddress($row);                   }                        $this->mail->Subject = $this->subject;        $this->mail->Body ="Hello \r\n\r\nWelcome to linkpan explore new things here and some cool features are also available. \r\n\r\nYour Business Model is promising with Linkpan, We focus on Transforming Your Business, by providing value, reinvent your processes and strengthen your capabilities and stimulate Your Core. Our architecture helps to promote your business drive, one-minute deal options, and network growth. We seek to know more, reinvent the wheel and discard common principles even if they prove otherwise Ethics Regulation: Doing Business with good faith, Trust and transparency, we know how hard it is to find a trustworthy Partner for business; we work on developing the best possibilities to make this goal even seamless, worry-free as possible. \r\n\r\nYou can register here ".FRONT_SIGNUP_VIEW_LINK." \r\n\r\n Regards \r\n Linkpan team.";         $this->mail->AltBody = "";          if (!$this->mail->send()) {            return "Mailer Error: " . $this->mail->ErrorInfo;        } else {            return true;        }    }    public function resetPasswordMail($email,$url) {                                $this->sender_email ='info@codexives.in';         $this->sender_name ='Linkpan';                $this->subject ='Forgot password';        $this->mail->isSMTP();        $this->mail->SMTPDebug = 0;        $this->mail->Debugoutput = 'html';        $this->mail->Host = $this->host;        $this->mail->Port = $this->port;        $this->mail->SMTPAuth = true;        $this->mail->SMTPSecure = true;        $this->mail->Username = $this->username;        $this->mail->Password = $this->password;        $this->mail->setFrom($this->sender_email);        $this->mail->addReplyTo($this->sender_email);        $this->mail->addAddress($email);                   $this->mail->Subject = $this->subject;        $this->mail->Body ='Your reset password link click here:'.$url;         $this->mail->AltBody = "";          if (!$this->mail->send()) {            return "Mailer Error: " . $this->mail->ErrorInfo;        } else {            return true;        }    }}?>